import React from 'react'
import { useState } from 'react';

export default function Main (props) {
    return (
        <div className="container my-3">
            <h3 className="text-center">Ratings For User</h3>
            
            <div className="dropdown">
            <label className = "btn-sm col-xs-6 margin-left" htmlFor=""><h5>Task 1: </h5></label>
  <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton2" data-bs-toggle="dropdown" aria-expanded="false">
    Dropdown button
  </button>
  <ul className="dropdown-menu dropdown-menu-dark" aria-labelledby="dropdownMenuButton2">
    <li><a className="dropdown-item active" href="#">0</a></li>
    <li><a className="dropdown-item" href="#">1</a></li>
    <li><a className="dropdown-item" href="#">2</a></li>
    {/* <li><hr className="dropdown-divider"/></li> */}
    {/* <li><a className="dropdown-item" href="#">Separated link</a></li> */}
  </ul>
  <select className="1-5"></select>
  <input type="text" id="dropdown1" />
</div>
        </div>
    )
}

{/* <script>
$(function(){
    var $select = $(".1-5");
    for (i=1;i<=5;i++){
        $select.append($('<option></option>').val(i).html(i))
    }
});
</script> */}
module DropDownListComponent {
    var items = [
      { text: "ListItem 1", value: "item1" },
      { text: "ListItem 2", value: "item2" },
      { text: "ListItem 3", value: "item3" },
      { text: "ListItem 4", value: "item4" },
      { text: "ListItem 5", value: "item5" }
    ];
$(function () {     
    var sample = new ej.DropDownList($("#dropdown1"),{   
        dataSource: items,
        fields: { text: "text", value: "value" }
    });
//Control focus key
$(document).on("keydown", function (e) {
    if (e.altKey && e.keyCode === 74) { // j- key code.
        $("#dropdown1_wrapper").focus();
    }
  });
});
}
