import React, {Component } from 'react'
import { Dimensions, StyleSheet, Text, View } from 'react-native';
import logo from './images/superAdmin.png';
class AdminLogin extends React.Component {
  constructor()
  {
    super();
    this.state={
      name: '',
      pwd: ''
    }

  }
  
  updateValue(text, field){
  if(field == 'name'){
    this.setState({
      name:text
    })
  }
  else if(field == 'pwd'){
    this.setState({
      pwd:text
    })
  }
    // console.log(field,text);
  }
  
submit(event){
event.preventDefault();
let collection = {}
collection.name = this.state.name;
collection.pwd = this.state.pwd;
console.log(collection);

}

  render(){
  return (
     
<div>
<nav className="navbar navbar-expand-lg navbar navbar-dark bg-primary">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">My Skill</a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                {/* <Link to = "./AdminLogin">Home</Link> */}
                <a className="nav-link active" aria-current="page" href="./SignUp">Sign Up</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">About</a>
              </li>
             
            </ul>
          </div>
        </div>
      </nav>
<h2 className= "text-center">Admin Login Form</h2>

    <form onSubmit={this.submit}>
        <div className="imgcontainer">
            <img src={logo} alt="Avatar" className="avatar"/>
        </div>
  
        <div className="container inputOne text-center">
            <label><b>Username</b></label><br />
            <input type="text" placeholder="Enter Username" name="uname" onChange = {(text) => this.updateValue(text.target.value, 'name')} className="formInput" required/><br />
  
            <label><b>Password</b></label><br />
            <input type="password" placeholder="Enter Password" name="psw" onChange = {(text) => this.updateValue(text.target.value, 'pwd')} className="formInput" required/><br /><br />
  
            <button type="submit" >Login</button><br />
            <input type="checkbox" /> Remember me
        </div>
        <div className="container" styles="background-color:#f1f1f1">
            {/* <button type="button" className="cancelbtn">Cancel</button> */}
            <span className="psw"><a href="#">Forgot Password</a></span>
        </div>
    </form>
</div>

// </div>
    )
            }
}
AdminLogin.defaultProps = {
    title: "Skill Base",
    searchBar: true 
  }
  export default AdminLogin;
